import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import './style.css';

import LeaderboardPage from './pages/LeaderboardPage';
import GamePage from './pages/GamePage';
import HomePage from './pages/HomePage';

function AppLayout() {
  const location = useLocation();
  const isGamePage = location.pathname === '/game';

  return (
    <div className="App p-4 min-h-screen bg-sky-100">
      {!isGamePage && (
        <>
          <nav className="mb-6 text-center">
            <Link to="/home" className="mr-4 text-blue-600 hover:underline">Trang chủ</Link>
            <Link to="/leaderboard" className="text-blue-600 hover:underline">Bảng xếp hạng</Link>
          </nav>
        </>
      )}

      <Routes>
        <Route path="/game" element={<GamePage />} />
        <Route path="/leaderboard" element={<LeaderboardPage />} />
        <Route path="/home" element={<HomePage />} />
        <Route path="*" element={<GamePage />} /> {/* Mặc định vào game */}
      </Routes>
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppLayout />
    </Router>
  );
}

export default App;
