import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './LeaderboardTable.css';

const LeaderboardTable = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const res = await axios.get('http://localhost:3000/scores');
        setLeaderboard(res.data);
      } catch (err) {
        console.error('L·ªói khi l·∫•y b·∫£ng x·∫øp h·∫°ng:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, []);
  const getTrophy = (index) => {
    if (index === 0) return <span className="emoji">üèÜ</span>;
    if (index === 1) return <span className="emoji">ü•à</span>;
    if (index === 2) return <span className="emoji">ü•â</span>;
    return null;
  };
  if (loading) return <p className="loading">‚è≥ ƒêang t·∫£i b·∫£ng x·∫øp h·∫°ng...</p>;

  return (
    <div className="leaderboard-container">
      <h2 className="leaderboard-title">üèÜ B·∫£ng x·∫øp h·∫°ng</h2>
      <table className="leaderboard-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Ng∆∞·ªùi ch∆°i</th>
            <th>ƒêi·ªÉm</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.map((user, index) => (
            <tr key={user.telegram_id}>
              <td>{getTrophy(index) || index + 1}</td>
              <td className="player">
                <img src={user.avatar} alt="avatar" />
                <span>{user.name}</span>
              </td>
              <td className="score">{user.score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
export default LeaderboardTable;
