import React, { useEffect, useRef } from 'react';

const GameCanvas = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    const sprite = new Image();
    sprite.src = '../../assets/sprite.png'; // Sprite sheet phải nằm trong public/assets/

    let game = 'start';
    let frame = 0;
    let arrGround = [];
    let arrPipes = [];

    const arrNumber = [
      { name: 0, sX: 1013, sY: 181, sW: 52, sH: 80, cW: 52, cH: 80 },
      { name: 1, sX: 1080, sY: 181, sW: 32, sH: 80, cW: 32, cH: 80 },
      { name: 2, sX: 1127, sY: 181, sW: 52, sH: 79, cW: 52, cH: 79 },
      { name: 3, sX: 1184, sY: 181, sW: 52, sH: 79, cW: 52, cH: 79 },
      { name: 4, sX: 1013, sY: 265, sW: 52, sH: 79, cW: 52, cH: 79 },
      { name: 5, sX: 1070, sY: 265, sW: 52, sH: 79, cW: 52, cH: 79 },
      { name: 6, sX: 1127, sY: 265, sW: 52, sH: 79, cW: 52, cH: 79 },
      { name: 7, sX: 1184, sY: 265, sW: 52, sH: 79, cW: 52, cH: 79 },
      { name: 8, sX: 1013, sY: 349, sW: 52, sH: 79, cW: 52, cH: 79 },
      { name: 9, sX: 1070, sY: 349, sW: 52, sH: 79, cW: 52, cH: 79 },
    ];

    const random = (min, max) => Math.ceil(Math.random() * (max - min) + min);

    class Ground {
      constructor(cX, cY) {
        this.cX = cX;
        this.cY = cY;
        this.sX = 624;
        this.sY = 0;
        this.sW = 215;
        this.sH = 143;
        this.cW = 215;
        this.cH = 143;
        this.dx = -2;
      }
      draw() {
        ctx.drawImage(sprite, this.sX, this.sY, this.sW, this.sH, this.cX, this.cY, this.cW, this.cH);
      }
    }

    class Pipes {
      constructor(cX, cY, space) {
        this.cX = cX;
        this.cY = cY;
        this.cW = 82;
        this.cH = 710;
        this.space = space;
        this.sXt = 0;
        this.sYt = 0;
        this.sXb = 1261;
        this.sYb = 0;
        this.sW = 82;
        this.sH = 710;
        this.dx = -2;
      }
      draw() {
        ctx.drawImage(sprite, this.sXt, this.sYt, this.sW, this.sH, this.cX, this.cY, this.cW, this.cH);
        ctx.drawImage(sprite, this.sXb, this.sYb, this.sW, this.sH, this.cX, this.cY + this.cH + this.space, this.cW, this.cH);
      }
    }

    class Bird {
      constructor(cX, cY) {
        this.cX = cX;
        this.cY = cY;
        this.animate = [
          { sX: 840, sY: 0 },
          { sX: 900, sY: 0 },
          { sX: 960, sY: 0 },
        ];
        this.sW = 51;
        this.sH = 36;
        this.cW = 51;
        this.cH = 36;
        this.i = 0;
        this.v = 0;
        this.a = 0.5;
      }
      draw() {
        if (game === 'start' && frame % 35 === 0) this.i = (this.i + 1) % 3;
        if (game === 'play' && frame % 16 === 0) this.i = (this.i + 1) % 3;
        ctx.drawImage(sprite, this.animate[this.i].sX, this.animate[this.i].sY, this.sW, this.sH, this.cX, this.cY, this.cW, this.cH);
      }
      update() {
        if (game === 'play' || game === 'end') {
          this.v += this.a;
          this.cY += this.v;
          if (this.cY + this.cH >= 625) {
            game = 'end';
            this.v = 0;
            this.cY = 625;
          }
        }
      }
    }

    const bird = new Bird(150, canvas.height / 2 - 12);

    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      arrGround.forEach(g => g.draw());
      arrPipes.forEach(p => p.draw());
      bird.draw();
    };

    const update = () => {
      bird.update();
    };

    const loop = () => {
      frame++;
      draw();
      update();
      requestAnimationFrame(loop);
    };

    sprite.onload = () => {
      for (let i = 0; i < 4; i++) arrGround.push(new Ground(215 * i, 625));
      for (let i = 0; i < 4; i++) arrPipes.push(new Pipes(random(530, 600) * i, random(-660, -300), 200));
      loop();
    };

    const handleClick = (e) => {
      switch (game) {
        case 'start':
          game = 'play';
          break;
        case 'play':
          bird.v = -8;
          break;
        case 'end':
          game = 'start';
          bird.cY = canvas.height / 2 - 12;
          bird.v = 0;
          arrPipes = [];
          for (let i = 0; i < 4; i++) arrPipes.push(new Pipes(random(530, 600) * i, random(-660, -300), 200));
          break;
        default:
          break;
      }
    };

    canvas.addEventListener('click', handleClick);
    return () => canvas.removeEventListener('click', handleClick);
  }, []);

  return <canvas ref={canvasRef} width={688} height={625} />;
};

export default GameCanvas;
