import React, { useEffect, useState } from 'react';
import axios from 'axios';

const LeaderboardTable = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const res = await axios.get('http://localhost:3000/scores'); // API g·ªçi tr·ª±c ti·∫øp t·∫°i ƒë√¢y
        setLeaderboard(res.data);
      } catch (error) {
        console.error('L·ªói khi l·∫•y d·ªØ li·ªáu b·∫£ng x·∫øp h·∫°ng:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, []);

  if (loading) return <p className="text-center mt-4">‚è≥ ƒêang t·∫£i b·∫£ng x·∫øp h·∫°ng...</p>;

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">üèÜ B·∫£ng x·∫øp h·∫°ng</h2>
      <table className="w-full table-auto border-collapse border border-gray-300 rounded-xl shadow">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-4 py-2 text-left">#</th>
            <th className="border px-4 py-2 text-left">Ng∆∞·ªùi ch∆°i</th>
            <th className="border px-4 py-2 text-right">ƒêi·ªÉm cao</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.map((user, index) => (
            <tr key={user.telegram_id} className="hover:bg-yellow-50">
              <td className="border px-4 py-2">{index + 1}</td>
              <td className="border px-4 py-2 flex items-center gap-2">
                <img src={user.avatar} alt="avatar" className="w-6 h-6 rounded-full" />
                {user.name}
              </td>
              <td className="border px-4 py-2 text-right">{user.high_score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default LeaderboardTable;
