import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './LeaderboardTable.css';

const LeaderboardTable = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const res = await axios.get('http://localhost:3000/scores');
        setLeaderboard(res.data);
      } catch (error) {
        console.error('L·ªói khi l·∫•y b·∫£ng x·∫øp h·∫°ng:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, []);

  if (loading) return <p style={{ textAlign: 'center', marginTop: '30px' }}>‚è≥ ƒêang t·∫£i...</p>;

  return (
    <div className="leaderboard-container">
      <div className="leaderboard-header">üèÜ B·∫£ng X·∫øp H·∫°ng</div>
      <div className="leaderboard-table">
        {leaderboard.map((user, index) => (
          <div key={user.telegram_id} className="leaderboard-row">
            <div className="rank-badge">{index + 1}</div>
            <img src={user.avatar} alt="avatar" className="avatar" />
            <div className="player-info">
              <div className="name">{user.name}</div>
            </div>
            <div className="coins">{user.score}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LeaderboardTable;
