import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './LeaderboardTable.css';


const LeaderboardTable = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const res = await axios.get(`http://localhost:3000/scores`);
        setLeaderboard(res.data);
      } catch (error) {
        console.error('L·ªói khi l·∫•y b·∫£ng x·∫øp h·∫°ng:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaderboard();
  }, []);

  if (loading) return <p className="text-center mt-6 text-gray-600">‚è≥ ƒêang t·∫£i b·∫£ng x·∫øp h·∫°ng...</p>;

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow-xl rounded-xl mt-10">
      <h2 className="text-2xl font-bold text-center text-yellow-600 mb-6">üèÜ B·∫£ng x·∫øp h·∫°ng</h2>
      <table className="w-full table-auto border-collapse">
        <thead>
          <tr className="bg-yellow-100 text-yellow-900 text-sm uppercase">
            <th className="p-3 text-left">#</th>
            <th className="p-3 text-left">Ng∆∞·ªùi ch∆°i</th>
            <th className="p-3 text-right">ƒêi·ªÉm</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.map((user, index) => (
            <tr
              key={user.telegram_id}
              className="border-t hover:bg-yellow-50 transition duration-200"
            >
              <td className="p-3">{index + 1}</td>
              <td className="p-3 flex items-center gap-2">
                <img
                  src={user.avatar}
                  alt="avatar"
                  className="w-8 h-8 rounded-full border border-yellow-300"
                />
                <span className="font-medium">{user.name}</span>
              </td>
              <td className="p-3 text-right font-semibold text-yellow-700">{user.score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default LeaderboardTable;
